import React, { useEffect, useState } from "react";
import DatePicker from "react-datepicker";
import styled from "styled-components";
import "react-datepicker/dist/react-datepicker.css";
import { formatDate } from "./utils/Dates";

const Container = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  align-items: center;
  margin: 0 auto;
`;

const MainTitle = styled.h1`
  display: flex;
  color: white;
  align-items: center;
`;
const Copyright = styled.p`
  display: flex;
  color: white;
  align-items: center;
`;

const ImageTitle = styled.h2`
  color: lightblue;
`;

const DatePickerStyled = styled(DatePicker)`
  border-radius: 10px;
  font-size: 20px;
  text-align: center;
  padding: 5px 10px 5px 10px;
  box-sizing: content-box;
  width: 120px;
  margin-left: 10px;
  font-weight: bold;
`;

const ImageContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-content: flex-start;
  justify-content: center;
`;

const Image = styled.img`
  border-radius: 5px;
`;

export default () => {
  const [test, setTest] = useState<any>({});
  const [startDate, setStartDate] = useState<Date | null | undefined>(
    new Date()
  );

  const dateFormatted = formatDate(startDate ?? new Date());

  //FIXME: API DATA WEBSITE === https://api.nasa.gov/index.html
  const API_KEY: string = "api_key=J9c8DvGBTzcqvhFd8UPQIJqhzJ80Ractk3JBOiRx";
  const baseUrl = "https://api.nasa.gov";

  const fetchData = async () => {
    const response = await fetch(
      `${baseUrl}/planetary/apod?date=${dateFormatted}&${API_KEY}`
    );
    const data = await response.json();
    setTest(data);
    console.log("datos", test);
  };

  useEffect(() => {
    fetchData();
  }, [dateFormatted]);

  return (
    <div>
      <Container>
        <MainTitle>
          {`Astronomy Picture of the`}
          <DatePickerStyled
            selected={startDate}
            onChange={(date: Date) => setStartDate(date)}
          />
        </MainTitle>
        <ImageTitle>{test.title}</ImageTitle>
        <ImageContainer>
          {test.url !== undefined && test.url.includes("youtube") ? (
            <iframe width="420" height="315" src={test.url}></iframe>
          ) : (
            <Image width="50%" height="50%" src={`${test.url}`} />
          )}
        </ImageContainer>
        <Copyright>{test.copyright}</Copyright>
      </Container>
    </div>
  );
};
